import React, { useEffect, useState, useRef } from 'react';

interface IndexData {
  ticker: string;
  change: string;
}

const GROUP_SIZE = 5;
const TOTAL_COUNT = 30;
const INTERVAL_MS = 3000;

const IndicesBar: React.FC = () => {
  const [indices, setIndices] = useState<IndexData[]>([]);
  const [groupIdx, setGroupIdx] = useState(0);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    fetch('/api/top-movers')
      .then(res => res.json())
      .then(data => setIndices(Array.isArray(data.movers) ? data.movers.slice(0, TOTAL_COUNT) : []));
  }, []);

  useEffect(() => {
    if (indices.length === 0) return;
    intervalRef.current = setInterval(() => {
      setGroupIdx(idx => (idx + 1) % Math.ceil(indices.length / GROUP_SIZE));
    }, INTERVAL_MS);
    return () => {
      if (intervalRef.current) clearInterval(intervalRef.current);
    };
  }, [indices]);

  const start = groupIdx * GROUP_SIZE;
  const end = start + GROUP_SIZE;
  const currentGroup = indices.slice(start, end);

  return (
    <div className="sticky top-[106px] z-40 w-full h-9 bg-gradient-to-r from-blue-50 to-blue-100 shadow flex items-center px-6 gap-8 overflow-x-auto border-b border-blue-100">
      {currentGroup.map(idx => (
        <div className="flex flex-row items-center gap-2 min-w-[90px]" key={idx.ticker}>
          <span className="text-gray-800 font-semibold text-base">{idx.ticker}</span>
          <span className={idx.change && idx.change.startsWith('+') ? "text-green-600 font-semibold text-sm" : "text-red-500 font-semibold text-sm"}>{idx.change}</span>
        </div>
      ))}
    </div>
  );
};

export default IndicesBar;
