export const headline = "font-extrabold text-xl bg-gradient-to-r from-blue-600 via-indigo-500 to-blue-400 bg-clip-text text-transparent group-hover:underline leading-snug mb-1";
export const summary = "text-gray-700 text-base leading-relaxed";
export const date = "text-xs text-gray-400 mt-1";
export const pagination = "flex justify-center gap-2 mt-8";
