import React, { useEffect, useState } from 'react';
import '../css/NewsList.tw.ts';

interface NewsItem {
  headline: string;
  source: string;
  url: string;
  summary: string;
  headline_ko: string;
  summary_ko: string;
}

const PAGE_SIZE = 15;

const NewsList: React.FC = () => {
  const [news, setNews] = useState<NewsItem[]>([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    fetch(`/api/news?page=${page}&limit=${PAGE_SIZE}`)
      .then(res => res.json())
      .then(data => {
        setNews(data.news || []);
        if (data.totalCount) {
          setTotalPages(Math.ceil(data.totalCount / PAGE_SIZE));
        }
      });
  }, [page]);

  const handlePrev = (e?: React.MouseEvent) => {
    if (e) e.preventDefault();
    setPage(p => Math.max(1, p - 1));
  };

  const handleNext = (e?: React.MouseEvent) => {
    if (e) e.preventDefault();
    setPage(p => Math.min(totalPages, p + 1));
  };

  const renderPagination = () => {
    const pages: (number | string)[] = [];
    const maxPage = totalPages;
    const current = page;

    // Always show first page
    pages.push(1);

    // Calculate start and end for the window
    let start = Math.max(2, current - 3);
    let end = Math.min(maxPage - 1, current + 3);

    // Adjust window if near the start
    if (current <= 4) {
      start = 2;
      end = Math.min(7, maxPage - 1);
    }
    // Adjust window if near the end
    else if (current >= maxPage - 3) {
      start = Math.max(2, maxPage - 6);
      end = maxPage - 1;
    }

    // Add ... after 1 if needed
    if (start !== 2) pages.push('...');

    // Add page numbers in window
    for (let i = start; i <= end; i++) {
      pages.push(i);
    }

    // Add ... before last page if needed
    if (end < maxPage - 1) {
      pages.push('...');
    }

    // Always show last page if more than 1
    if (maxPage > 1) pages.push(maxPage);

    // Ensure max 9 items (excluding Prev/Next)
    while (pages.length > 9) {
      if (end - start > 4) {
        if (current - start > end - current) {
          start++;
          pages.splice(1, 1);
        } else {
          end--;
          pages.splice(pages.length - (pages[pages.length - 2] === '...' ? 3 : 2), 1);
        }
      } else {
        break;
      }
    }

    return (
      <div className="news-pagination-advanced">
        <button
          type="button"
          onClick={handlePrev}
          disabled={page === 1}
          className="news-pagination-btn"
        >
          &lt; Prev
        </button>
        {pages.map((p, idx) =>
          p === '...'
            ? (
              <button
                key={`ellipsis-${idx}`}
                className="news-pagination-btn news-pagination-ellipsis"
                disabled
                tabIndex={-1}
                type="button"
              >
                ...
              </button>
            )
            : (
              <button
                type="button"
                key={`page-${p}`}
                className={`news-pagination-btn${p === page ? ' active' : ''}`}
                onClick={() => setPage(Number(p))}
                disabled={p === page}
              >
                {p}
              </button>
            )
        )}
        <button
          type="button"
          onClick={handleNext}
          disabled={page === totalPages}
          className="news-pagination-btn"
        >
          Next &gt;
        </button>
      </div>
    );
  };

  return (
    <div className="news-list">
      <div className="news-list-header">
        <h3>주요뉴스</h3>
      </div>
      <div className="news-card-list">
        {news.filter(item => item.headline && item.summary).map(item => (
          <div className="news-card" key={item.url}>
            <a href={item.url} target="_blank" rel="noopener noreferrer" className="news-headline">
              {item.headline}
            </a>
            <p className="news-summary">{item.summary}</p>
          </div>
        ))}
      </div>
      {renderPagination()}
    </div>
  );
};

export default NewsList;
