import React, { useState, useRef, useEffect } from 'react';
import Header from './components/Header';
import IndicesBar from './components/IndicesBar';
import NewsList from './components/NewsList';
import TopMovers from './components/TopMovers';
import Chatbot from './components/Chatbot';
import Analysis from './components/Analysis';
import * as tw from './css/App.tw';

function App() {
  const [tab, setTab] = useState<'news' | 'analysis'>('news');

  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div className={tw.root}>
      <Header />
      {/* 메뉴바 */}
      <div className={tw.menuBar}>
        <button
          className={
            tw.menuBtn + ' ' + (tab === 'news' ? tw.menuBtnActive : tw.menuBtnInactive)
          }
          onClick={() => setTab('news')}
        >
          뉴스
        </button>
        <button
          className={
            tw.menuBtn + ' ' + (tab === 'analysis' ? tw.menuBtnActive : tw.menuBtnInactive)
          }
          onClick={() => setTab('analysis')}
        >
          기업분석
        </button>
      </div>
      {/* 지수바 */}
      <IndicesBar />
      {/* 본문 */}
      {tab === 'news' ? (
        <div className={tw.mainContent}>
          <section className={tw.section}>
            <NewsList />
          </section>
          <aside className={tw.aside}>
            <Chatbot />
          </aside>
        </div>
      ) : (
        <div className={tw.analysisContent}>
          <section className={tw.analysisSection}>
            <Analysis />
          </section>
        </div>
      )}
    </div>
  );
}
export default App;
