import React, { useEffect, useState } from 'react';
import * as tw from '../css/NewsList.tw';

interface NewsItem {
  headline_ko: string;
  summary_ko: string;
  url: string;
}

const PAGE_SIZE = 15;

const NewsList: React.FC = () => {
  const [news, setNews] = useState<NewsItem[]>([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    fetch(`/api/news?page=${page}&limit=${PAGE_SIZE}`)
      .then(res => res.json())
      .then(data => {
        console.log('뉴스 응답:', data); // 응답 구조 확인
        setNews(data.news || []);
        if (data.totalCount) {
          setTotalPages(Math.ceil(data.totalCount / PAGE_SIZE));
        }
      })
      .catch(err => {
        console.error('뉴스 불러오기 실패:', err);
      });
  }, [page]);

  const renderPagination = () => {
    const pages = [];
    const maxPage = totalPages;
    const current = page;
    const windowSize = 5;
    let start = Math.max(1, current - Math.floor(windowSize / 2));
    let end = Math.min(maxPage, start + windowSize - 1);
    if (end - start < windowSize - 1) start = Math.max(1, end - windowSize + 1);

    if (current > 1) {
      pages.push(
        <button key="prev" className={tw.pageBtn} onClick={() => setPage(current - 1)}>&lt;</button>
      );
    }
    for (let i = start; i <= end; i++) {
      pages.push(
        <button
          key={i}
          className={`${tw.pageBtn} ${i === current ? tw.pageBtnActive : ""}`}
          onClick={() => setPage(i)}
          disabled={i === current}
        >
          {i}
        </button>
      );
    }
    if (current < maxPage) {
      pages.push(
        <button key="next" className={tw.pageBtn} onClick={() => setPage(current + 1)}>&gt;</button>
      );
    }
    return <div className={tw.pagination}>{pages}</div>;
  };

  return (
    <div className={tw.container}>
      {news.map(item => (
        <a href={item.url} target="_blank" rel="noopener noreferrer" className={tw.newsItem} key={item.url}>
          <div className={tw.headline}>{item.headline_ko}</div>
          <div className={tw.summary}>{item.summary_ko}</div>
        </a>
      ))}
      {renderPagination()}
    </div>
  );
};

export default NewsList;
